from datetime import datetime, timedelta, timezonefrom typing import Any, Union, Optionalfrom jose import jwt, JWTErrorfrom passlib.context import CryptContextfrom app.core.config import settingspwd_context = CryptContext(schemes=["bcrypt"], deprecated="auto")def verify_password(plain_password: str, hashed_password: str) -> bool:    """Проверка пароля"""    return pwd_context.verify(plain_password, hashed_password)def get_password_hash(password: str) -> str:    """Хеширование пароля"""    return pwd_context.hash(password)def create_access_token(subject: Union[str, Any], expires_delta: Optional[timedelta] = None) -> str:    """Создание access токена"""    if expires_delta:        expire = datetime.now(timezone.utc) + expires_delta    else:        expire = datetime.now(timezone.utc) + timedelta(minutes=settings.ACCESS_TOKEN_EXPIRE_MINUTES)        to_encode = {        "exp": expire,        "sub": str(subject),        "type": "access"    }    return jwt.encode(to_encode, settings.SECRET_KEY, algorithm=settings.ALGORITHM)def create_refresh_token(subject: Union[str, Any], expires_delta: Optional[timedelta] = None) -> str:    """Создание refresh токена"""    if expires_delta:        expire = datetime.now(timezone.utc) + expires_delta    else:        expire = datetime.now(timezone.utc) + timedelta(minutes=settings.REFRESH_TOKEN_EXPIRE_MINUTES)        to_encode = {        "exp": expire,        "sub": str(subject),        "type": "refresh"    }    return jwt.encode(to_encode, settings.SECRET_KEY, algorithm=settings.ALGORITHM)def decode_token(token: str) -> Optional[dict]:    """Декодирование токена"""    try:        payload = jwt.decode(token, settings.SECRET_KEY, algorithms=[settings.ALGORITHM])        return payload    except JWTError:        return None